        )
    {
        total = rewardsSchedule(0, startTime, endTime);
        scheduleCalculated =
            rewardsSchedule(0, startTime, endTime) /
            1000000000000000000;
        if (totalAuroraShares != 0) {
            rewardPerShareAurora = total / (totalAuroraShares);
        } else {
            rewardPerShareAurora = total;
        }
