    function batchClaimOnBehalfOfOtherUsers(
        address[] memory accounts,
        uint256[] memory streamIds
    ) external pausable(1) onlyRole(CLAIM_ROLE) {
        _before();
        for (uint256 i = 0; i < accounts.length; i++) {
